INSERT INTO `wp_postmeta` VALUES (660,133,'accordions_0_accordion_description','<p data-uw-styling-context=\"true\">Upon application, the customer needs to understand the following terms before signing up:</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\"><b data-uw-styling-context=\"true\">Policy Term (Ranging from 9 to 18 years)</b>: This is the entire period where the policyholder pays the premium and receives the benefits. The policyholder is usually covered for the term duration.</li>\r\n 	<li data-uw-styling-context=\"true\"><b data-uw-styling-context=\"true\">Premium Paying Term (Ranging from 5 to 14 years)</b>: This is the period the policyholder pays the premium before the maturity.</li>\r\n 	<li data-uw-styling-context=\"true\"><b data-uw-styling-context=\"true\">Policy Maturity (4 years)</b>: The product pays pre-defined benefits in 4 years. 15% of sum assured each and a final payout of 45% sum assured (105% of sum assured plus bonus). Minimum premium is Ksh 2,000.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (661,133,'_accordions_0_accordion_description','field_624244aa1e3ef');
