INSERT INTO `wp_postmeta` VALUES (757,142,'accordions_3_accordion_description','<p data-uw-styling-context=\"true\">LPO Financing is a form of short-term financing designed for suppliers whereby funds are advanced against local purchase orders that suppliers will have received to supply certain goods or services.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\"><strong>Benefits:</strong></h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Provides working capital to execute the contract</li>\r\n 	<li data-uw-styling-context=\"true\">Faster/timely execution of the contract as a result of the access to funds.</li>\r\n</ul>');
