INSERT INTO `wp_postmeta` VALUES (1248,194,'accordions_4_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">ID of principle member</li>\r\n 	<li data-uw-styling-context=\"true\">ID of spouse</li>\r\n 	<li data-uw-styling-context=\"true\">ID of children above 18 years with school ID/admission letter for those above 21 years</li>\r\n 	<li data-uw-styling-context=\"true\">Birth certificate/notification for any other dependent</li>\r\n 	<li data-uw-styling-context=\"true\">Minimum joining age for children is at birth and maximum limit is 70 years. An applicant who has attained 70 years will exit at age 80 years</li>\r\n 	<li data-uw-styling-context=\"true\">Children over 21 years are covered up to 25 years with proof of schooling.</li>\r\n 	<li data-uw-styling-context=\"true\">Premiums are based on the age of the applicant</li>\r\n 	<li data-uw-styling-context=\"true\">For any additional child over the family size of 8 an additional premium of Kshs 2,500 Kshs.2,000 and 1,500 respectively</li>\r\n 	<li data-uw-styling-context=\"true\">Children over 21 years are covered up to 25 years with proof of schooling.</li>\r\n 	<li data-uw-styling-context=\"true\">Premiums are based on the age of the applicant</li>\r\n 	<li data-uw-styling-context=\"true\">For any additional child over the family size of 8 an additional premium of Kshs 2,500 Kshs.2,000 and 1,500 respectively</li>\r\n</ul>');
