INSERT INTO `wp_postmeta` VALUES (1251,194,'_accordions_5_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (1252,194,'accordions_5_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Pre-existing, chronic conditions and cancers, on full disclosure at the time of joining and subject to 90 days waiting period will be covered but limited to 50% of the impatient limit.</li>\r\n 	<li data-uw-styling-context=\"true\">Newly diagnosed chronic conditions will be covered in full within the impatient benefit.</li>\r\n 	<li data-uw-styling-context=\"true\">HIV/AIDS conditions will be covered 50% of the inpatient limit subject to a waiting period of 90 days.</li>\r\n 	<li data-uw-styling-context=\"true\">Maternity and pregnancy related conditions, normal delivery  KSh 10,000. Subject to a 12 months waiting period.</li>\r\n 	<li data-uw-styling-context=\"true\">Caesarean section between Kshs 30,000 and 20,000 subject to a 12 months waiting period</li>\r\n 	<li data-uw-styling-context=\"true\">Day care surgery(subject to specific pre-authorization)</li>\r\n 	<li data-uw-styling-context=\"true\">Inpatient non accidental dental and optical cover of between Kshs. 50,000 to Kshs.15,000 per family depending on the option chosen.</li>\r\n 	<li data-uw-styling-context=\"true\">Annual pap smear &amp; prostrate test for the couple</li>\r\n 	<li data-uw-styling-context=\"true\">Accidental death of the principle member &amp; spouse with a limit of between Kshs 300,000 &amp; kshs.100,000</li>\r\n 	<li data-uw-styling-context=\"true\">Accidental permanent disability of the principal member &amp; spouse with a limit of between Kshs. 300,000 &amp; kshs.100,000</li>\r\n 	<li data-uw-styling-context=\"true\">Last expense cover per family with a limit of kshs 50,000, 30,000 or 15,000 (the last expense cover is payable on the first death of any of the declared nuclear family members).</li>\r\n 	<li data-uw-styling-context=\"true\">24 hour emergency/help line.</li>\r\n</ul>');
