INSERT INTO `wp_postmeta` VALUES (1323,195,'accordions_2_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Self referred or self prescribed treatment</li>\r\n 	<li data-uw-styling-context=\"true\">Family planning, infertility &amp; impotence</li>\r\n 	<li data-uw-styling-context=\"true\">Intentional self- injury, chronic drunkenness, suicide or attempted suicide, drug and substance abuse, hazardous pursuits(sports and hobbies)</li>\r\n 	<li data-uw-styling-context=\"true\">Cosmetic and beauty treatment (unless necessitated by accidental injury)</li>\r\n 	<li data-uw-styling-context=\"true\">Outpatient ambulance services</li>\r\n 	<li data-uw-styling-context=\"true\">Experimental treatment or treatment subject to medical research</li>\r\n 	<li data-uw-styling-context=\"true\">Weight management treatment and drugs</li>\r\n 	<li data-uw-styling-context=\"true\">Diagnostic equipment(glucometers, BP Machines etc and hearing aids</li>\r\n 	<li data-uw-styling-context=\"true\">General medical checkups not incidental to diagnosis of an illness or Injury</li>\r\n 	<li data-uw-styling-context=\"true\">External surgical appliances(crutches and wheelchairs and prosthesis</li>\r\n 	<li data-uw-styling-context=\"true\">Dental prosthesis, crowns, dentures, bridges and braces</li>\r\n 	<li data-uw-styling-context=\"true\">Alternative medicine ( acupuncture, chiropractor, herbal medicine).</li>\r\n 	<li data-uw-styling-context=\"true\">Treatment outside the appointed panel of service providers</li>\r\n 	<li data-uw-styling-context=\"true\">Nutritional supplements unless prescribed as part of medical treatment of specified conditions</li>\r\n 	<li data-uw-styling-context=\"true\">Adult vaccinations and private vaccines for children.</li>\r\n 	<li data-uw-styling-context=\"true\">Costs of donor and related cost transplant for organ transplant</li>\r\n 	<li data-uw-styling-context=\"true\">Optical services including eg eye glasses/lenses/frames, eye testing, transplants/grafts and laser surgery</li>\r\n</ul>');
