INSERT INTO `wp_postmeta` VALUES (1327,195,'accordions_3_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Waiting period is not applicable for accidental cases or persons transferring from another Medical Insurance Policy</li>\r\n 	<li data-uw-styling-context=\"true\">Outpatient treatment can only be purchased at the inception/renewal of the policy and not during the term of the policy</li>\r\n 	<li data-uw-styling-context=\"true\">Members not subjected to medicals tests on joining.</li>\r\n 	<li data-uw-styling-context=\"true\">No cash deposit required and no contribution to the bill by the customer.</li>\r\n 	<li data-uw-styling-context=\"true\">Extensive service provider/Hospital network</li>\r\n</ul>');
