INSERT INTO `wp_postmeta` VALUES (1331,195,'accordions_4_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Present your membership smart card at the service providers (doctor, clinic or hospital) facilitys desk for identification.</li>\r\n 	<li data-uw-styling-context=\"true\">Your membership will be checked and confirmed using your smartcard and fingerprints.</li>\r\n 	<li data-uw-styling-context=\"true\">Complete and sign the claim form which will be made available at the service providers facility. The attending physician/specialist must also complete and sign the claim form. A claim form must be completed for each episode of treatment and for each person.</li>\r\n 	<li data-uw-styling-context=\"true\">All bills will be sent by the service provider directly.</li>\r\n</ul>');
