INSERT INTO `wp_postmeta` VALUES (1335,195,'accordions_5_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Pays for hospitalization medical expenses</li>\r\n 	<li data-uw-styling-context=\"true\">Provides supplementary benefits within the main covers which includes Pre-existing conditions, Chronic conditions, HIV/AIDS, Maternity, Inpatient Dental &amp; Ophthalmology(eye disease)</li>\r\n 	<li data-uw-styling-context=\"true\">Funeral expenses cover.</li>\r\n 	<li data-uw-styling-context=\"true\">Enables valid members to obtain quality Healthcare from private providers</li>\r\n 	<li data-uw-styling-context=\"true\">Available to customers/employees and their immediate dependents</li>\r\n 	<li data-uw-styling-context=\"true\">Access to 24 hour medical treatment</li>\r\n 	<li data-uw-styling-context=\"true\">No cash deposit</li>\r\n 	<li data-uw-styling-context=\"true\">No contribution to the bill by the customers</li>\r\n 	<li data-uw-styling-context=\"true\">Various options available</li>\r\n 	<li data-uw-styling-context=\"true\">Wide scope of cover</li>\r\n 	<li data-uw-styling-context=\"true\">Dependent children can be covered on their own, provided the parent proves that they are covered elsewhere e.g. by an employer and the children are not included.</li>\r\n 	<li data-uw-styling-context=\"true\">Mixing of both inpatient and outpatient options.</li>\r\n</ul>');
