INSERT INTO `wp_postmeta` VALUES (1374,206,'accordions_0_accordion_description','<p data-uw-styling-context=\"true\">We provide you with customized quality insurance policies that give you peace of mind as you make your business decisions.</p>\r\n<p data-uw-styling-context=\"true\">Our Policies include:</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Motor commercial  own goods</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\">This covers the insured against loss or damage to vehicle used to carry goods belonging to insured.</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Motor commercial  general cartage</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\">This covers the insured against loss or damage to vehicle used to carry goods belonging to third party</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Motor trade- selling of vehicles</li>\r\n 	<li data-uw-styling-context=\"true\">Road risks</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\">This covers loss or damage to vehicles on the road in custody or control of the insured including liabilities to third parties</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Internal risks</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\">This covers loss or damage while in the insureds premises including in the open yard to own vehicle(s) or vehicles held on the road in custody or control of the insured including liabilities to third parties.</p>');
