INSERT INTO `wp_postmeta` VALUES (1578,218,'accordions_0_accordion_description','<p data-uw-styling-context=\"true\">The coverage offered under<b data-uw-styling-context=\"true\">Take Me Home</b>eases the burden of repatriating an insured Kenyan deceased abroad, back home. This benefit ensures that the journey to their final resting place does not add an additional financial burden to the affected family and diaspora community.</p>\r\n<p data-uw-styling-context=\"true\">This benefits is on reinstatement basis and additional premium is payable on payment of a claim within the cover period.</p>');
