INSERT INTO `wp_postmeta` VALUES (1582,218,'accordions_1_accordion_description','<p data-uw-styling-context=\"true\">The coverage offered under the<b data-uw-styling-context=\"true\">Return Ticket</b>provides a solution for unexpected travel needs in the unfortunate loss of a nominated family member in Kenya. The benefit offers between Kes. 100,000 to Kes. 500,000 for traveling costs on reimbursement basis.</p>\r\n<p data-uw-styling-context=\"true\">This benefits is on reinstatement basis and additional premium is payable on payment of a claim within the cover period.</p>');
