INSERT INTO `wp_postmeta` VALUES (1586,218,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">Complete the application form and submit the executed form together with the supporting documents to your Diaspora Relationship Officer.</p>\r\n<p data-uw-styling-context=\"true\">Your application will then be processed and once approved you will receive a confirmation that your cover is active.</p>');
