INSERT INTO `wp_postmeta` VALUES (1590,218,'accordions_3_accordion_description','<p data-uw-styling-context=\"true\"><b data-uw-styling-context=\"true\"><u data-uw-styling-context=\"true\">TAKE ME HOME</u></b></p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Death claim form</li>\r\n 	<li data-uw-styling-context=\"true\">Notification through Kenya embassy</li>\r\n 	<li data-uw-styling-context=\"true\">Postmortem</li>\r\n 	<li data-uw-styling-context=\"true\">Certificate of identity.(Proof someone else knows the person)</li>\r\n 	<li data-uw-styling-context=\"true\">Claim settlement will made within 48 Hours on full documentation and return of duly executed discharge voucher.</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\"><b data-uw-styling-context=\"true\"><u data-uw-styling-context=\"true\">RETURN TICKET</u></b></p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Death claim form</li>\r\n 	<li data-uw-styling-context=\"true\">Postmortem</li>\r\n 	<li data-uw-styling-context=\"true\">Certificate of identity.(Proof someone else knows the person)</li>\r\n 	<li data-uw-styling-context=\"true\">Claim settlement will made within 48 Hours on full documentation and return of duly executed discharge voucher.</li>\r\n</ul>');
