INSERT INTO `wp_postmeta` VALUES (1613,219,'accordions_2_accordion_description','<table data-uw-styling-context=\"true\">\r\n<tbody data-uw-styling-context=\"true\">\r\n<tr data-uw-styling-context=\"true\">\r\n<th data-uw-styling-context=\"true\"></th>\r\n<th data-uw-styling-context=\"true\">Minimum (Years)</th>\r\n<th data-uw-styling-context=\"true\">Maximum (Years)</th>\r\n</tr>\r\n<tr data-uw-styling-context=\"true\">\r\n<td data-uw-styling-context=\"true\">Savings Period</td>\r\n<td data-uw-styling-context=\"true\">5</td>\r\n<td data-uw-styling-context=\"true\">18</td>\r\n</tr>\r\n<tr data-uw-styling-context=\"true\">\r\n<td data-uw-styling-context=\"true\">Benefits Period</td>\r\n<td data-uw-styling-context=\"true\">3</td>\r\n<td data-uw-styling-context=\"true\">8</td>\r\n</tr>\r\n<tr data-uw-styling-context=\"true\">\r\n<td data-uw-styling-context=\"true\">Entire Policy Term</td>\r\n<td data-uw-styling-context=\"true\">8</td>\r\n<td data-uw-styling-context=\"true\">24</td>\r\n</tr>\r\n</tbody>\r\n</table>');
