INSERT INTO `wp_postmeta` VALUES (1616,219,'_accordions_3_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (1617,219,'accordions_3_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Applicable for persons aged between age 18 to 65 years</li>\r\n 	<li data-uw-styling-context=\"true\">6 months waiting period where only claims from accidents are payable</li>\r\n 	<li data-uw-styling-context=\"true\">Premiums paid are fixed and can be done annually, semi-annually, quarterly or monthly.</li>\r\n 	<li data-uw-styling-context=\"true\">Policy acquires surrender value only after one year</li>\r\n 	<li data-uw-styling-context=\"true\">Premiums adjustment either upwards or downwards is allowed subjected to timely notification</li>\r\n 	<li data-uw-styling-context=\"true\">A cooling off period, where you can opt to cancel the policy, of thirty days is applicable.</li>\r\n 	<li data-uw-styling-context=\"true\">The policy acquires interest through bonuses paid periodically as announced and advised</li>\r\n 	<li data-uw-styling-context=\"true\">The policy allows you to take a loan against the amount saved.</li>\r\n</ul>');
