INSERT INTO `wp_postmeta` VALUES (1644,220,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">The policy also covers for other extra costs that the family may have to meet at the time of death. These include but are not limited to:</p>\r\n\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Refreshments at funeral</li>\r\n 	<li data-uw-styling-context=\"true\">Cost of buying wreath</li>\r\n 	<li data-uw-styling-context=\"true\">Cost of tent, seats etc.</li>\r\n 	<li data-uw-styling-context=\"true\">The hearse</li>\r\n 	<li data-uw-styling-context=\"true\">The cost of transportation of family members</li>\r\n 	<li data-uw-styling-context=\"true\">The casket or urn</li>\r\n 	<li data-uw-styling-context=\"true\">Purchase of burial plots in public cemeteries</li>\r\n 	<li data-uw-styling-context=\"true\">Clearance of all hospital bills inclusive of mortuary bills accrued within clause</li>\r\n 	<li data-uw-styling-context=\"true\">Labour for the burial site</li>\r\n 	<li data-uw-styling-context=\"true\">Obituaries and any other funeral announcements</li>\r\n 	<li data-uw-styling-context=\"true\">Cremations</li>\r\n 	<li data-uw-styling-context=\"true\">Costs of mortuary</li>\r\n</ul>');
