INSERT INTO `wp_postmeta` VALUES (1677,227,'values_0_value_description','<div style=\"text-align: left;\">\r\n<p data-uw-styling-context=\"true\">Co-operative Bank of South Sudan is a member of the Co-operative Bank of Kenya Group, headquartered in Nairobi, Kenya, with subsidiaries in Kenya and South Sudan. As of December 2013, the Groups asset base exceeded US$2.6 billion (KES:231.2 billion), with shareholders equity in excess of US$414 million (KES:36.8 billion). The shares of stock of the Cooperative Bank Group are listed on the Nairobi Stock Exchange, where it trades under the symbol: COOP.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Members of the Cooperative Bank Group include the following companies:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Kingdom Securities Limited  Nairobi, Kenya  60% shareholding</li>\r\n 	<li data-uw-styling-context=\"true\">Co-opTrust Investment Services Limited  Nairobi, Kenya  100% shareholding</li>\r\n 	<li data-uw-styling-context=\"true\">Co-operative Consultancy Services Kenya Limited  100% shareholding</li>\r\n 	<li data-uw-styling-context=\"true\">CIC Insurance Company Limited  25% shareholding</li>\r\n 	<li data-uw-styling-context=\"true\">Cooperative Bank of South Sudan  Juba, South Sudan  51% shareholding</li>\r\n</ul>\r\n</div>');
