INSERT INTO `wp_postmeta` VALUES (4579,369,'accordions_1_accordion_description','<p data-uw-styling-context=\"true\">Our clients can maintain a savings or current foreign currency account.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Low account maintenance fee</li>\r\n 	<li data-uw-styling-context=\"true\">Access to Internet Banking platform</li>\r\n 	<li data-uw-styling-context=\"true\">A monthly statement is available upon request</li>\r\n 	<li data-uw-styling-context=\"true\">The accounts are available in USD, EUR and GBP</li>\r\n</ul>');
