INSERT INTO `wp_postmeta` VALUES (4591,369,'accordions_4_accordion_description','<p data-uw-styling-context=\"true\">Send and receive money from friends and family in ten minutes<br data-uw-styling-context=\"true\" />Co-operative bank is an authorized MoneyGram agent. MoneyGram is an international money transfer service that offers you ten minutes money transfers from anywhere in the world.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Fast, safe and reliable money transfer option where only the beneficiary is paid</li>\r\n 	<li data-uw-styling-context=\"true\">Accessible at any Co-operative Bank of South Sudan branches</li>\r\n</ul>');
