INSERT INTO `wp_postmeta` VALUES (5608,389,'accordions_0_accordion_description','<p data-uw-styling-context=\"true\">Issued by the Bank at the request of the applicant (importer/applicant) and the Bank gives an irrevocable undertaking to pay the beneficiary/exporter for the goods or services.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Benefits:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Payment is guaranteed by both the issuing bank and the confirming bank as long as compliant documents are presented.</li>\r\n 	<li data-uw-styling-context=\"true\">Mitigate the buyer/country risks</li>\r\n 	<li data-uw-styling-context=\"true\">Ensure delivery is done within schedule.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (5609,389,'_accordions_0_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (5610,389,'accordions_1_accordion_title','Bank Guarantees');
INSERT INTO `wp_postmeta` VALUES (5611,389,'_accordions_1_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (5612,389,'accordions_1_accordion_description','<p data-uw-styling-context=\"true\">A written irrevocable undertaking from the Bank to pay the seller if the buyer defaults on his obligations.</p>\r\n<p data-uw-styling-context=\"true\">Some of the Bank Guarantees include the following:</p>\r\n\r\n<ol data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Bid/Tender Bonds</li>\r\n 	<li data-uw-styling-context=\"true\">Performance Bonds</li>\r\n 	<li data-uw-styling-context=\"true\">Advance Payment Guarantees</li>\r\n 	<li data-uw-styling-context=\"true\">Payment Guarantees</li>\r\n 	<li data-uw-styling-context=\"true\">Credit Guarantees</li>\r\n</ol>\r\n<h3 data-uw-styling-context=\"true\">Benefits:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Provides security for performance</li>\r\n 	<li data-uw-styling-context=\"true\">Reduce risk</li>\r\n 	<li data-uw-styling-context=\"true\">Give assurance of the serious intent and business standing of the party whose obligations are underwritten.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (5613,389,'_accordions_1_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (5614,389,'accordions_2_accordion_title','Invoice Discounting');
INSERT INTO `wp_postmeta` VALUES (5615,389,'_accordions_2_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (5616,389,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">With Co-op Banks Invoice Discounting, you can withdraw money against sales invoices before payments are done. This is available to various prequalified procuring entities like UNMISS, UNDP, UNMAS, UNIFSA, WFP etc.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\"><strong>Benefits:</strong></h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">This makes available 70-80% of the invoice value as soon as it has been raised and the remainder is made available when the customer pays.</li>\r\n 	<li data-uw-styling-context=\"true\">Enables you to unlock your business working capital and cash flow that would be obtained after a period of time depending on the terms of payment e.g. 30  45 days after invoicing.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (5617,389,'_accordions_2_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (5618,389,'accordions_3_accordion_title','Purchase Order Financing (LPO)');
INSERT INTO `wp_postmeta` VALUES (5619,389,'_accordions_3_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (5620,389,'accordions_3_accordion_description','<p data-uw-styling-context=\"true\">LPO Financing is a form of short-term financing designed for suppliers whereby funds are advanced against local purchase orders that suppliers will have received to supply certain goods or services.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\"><strong>Benefits:</strong></h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Provides working capital to execute the contract</li>\r\n 	<li data-uw-styling-context=\"true\">Faster/timely execution of the contract as a result of the access to funds.</li>\r\n</ul>');
