INSERT INTO `wp_postmeta` VALUES (5676,391,'accordions_3_accordion_description','<p data-uw-styling-context=\"true\">Co-operative Bank of South Sudan has partnered with Flex-Money to provide our customers with flexible options to transfer funds across the borders.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Available in SSP, USD, KES, and UGX</li>\r\n 	<li data-uw-styling-context=\"true\">A secure platform that is capable of sending and receiving cash pickup transactions in a matter of seconds.</li>\r\n 	<li data-uw-styling-context=\"true\">Globally accepted.</li>\r\n 	<li data-uw-styling-context=\"true\">SMS alerts whenever the money is received via mobile banking.</li>\r\n 	<li data-uw-styling-context=\"true\">Flexible forex rates and fees management.</li>\r\n</ul>');
