INSERT INTO `wp_postmeta` VALUES (6049,448,'_accordions_0_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6050,448,'accordions_0_accordion_description','<p data-uw-styling-context=\"true\">Take advantage of our competitive buying and selling rates across all our branches countrywide.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Available in all major currencies</li>\r\n 	<li data-uw-styling-context=\"true\">Unmatched competitive rates in the market</li>\r\n 	<li data-uw-styling-context=\"true\">Open to non-account holders</li>\r\n 	<li data-uw-styling-context=\"true\">Fast, efficient and reliable.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6051,448,'_accordions_0_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6052,448,'accordions_1_accordion_title','Savings Account');
INSERT INTO `wp_postmeta` VALUES (6053,448,'_accordions_1_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6054,448,'accordions_1_accordion_description','<p data-uw-styling-context=\"true\">Our clients can maintain a savings or current foreign currency account.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Low account maintenance fee</li>\r\n 	<li data-uw-styling-context=\"true\">Access to Internet Banking platform</li>\r\n 	<li data-uw-styling-context=\"true\">A monthly statement is available upon request</li>\r\n 	<li data-uw-styling-context=\"true\">The accounts are available in USD, EUR and GBP</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6055,448,'_accordions_1_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6056,448,'accordions_2_accordion_title','Swift Transfers');
INSERT INTO `wp_postmeta` VALUES (6057,448,'_accordions_2_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6058,448,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">SWIFT is a worldwide money transfer platform that allows you to send and receive money across the world in the shortest time possible using your bank account.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Convenient, secure payment mode to recipients account</li>\r\n 	<li data-uw-styling-context=\"true\">Send and receive money from all non-sanctioned countries.</li>\r\n 	<li data-uw-styling-context=\"true\">Enjoy service at exclusive teller counters</li>\r\n 	<li data-uw-styling-context=\"true\">A sending charge for as low as USD 35</li>\r\n 	<li data-uw-styling-context=\"true\">Personalized customer service and so much more</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6059,448,'_accordions_2_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6060,448,'accordions_3_accordion_title','Flex-Money');
INSERT INTO `wp_postmeta` VALUES (6061,448,'_accordions_3_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6062,448,'accordions_3_accordion_description','<p data-uw-styling-context=\"true\">The Co-operative Bank of South Sudan has partnered with Flex-Money to provide our customers with flexible options to transfer funds across the borders.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Available in SSP, USD, KES, and UGX</li>\r\n 	<li data-uw-styling-context=\"true\">A secure platform that is capable of sending and receiving cash pickup transactions in a matter of seconds.</li>\r\n 	<li data-uw-styling-context=\"true\">Globally accepted.</li>\r\n 	<li data-uw-styling-context=\"true\">SMS alerts whenever the money is received via mobile banking.</li>\r\n 	<li data-uw-styling-context=\"true\">Flexible forex rates and fees management.</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6063,448,'_accordions_3_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6064,448,'accordions_4_accordion_title','MoneyGram');
INSERT INTO `wp_postmeta` VALUES (6065,448,'_accordions_4_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6066,448,'accordions_4_accordion_description','<p data-uw-styling-context=\"true\">Send and receive money from friends and family in ten minutes<br data-uw-styling-context=\"true\" />Co-operative Bank is an authorized MoneyGram agent. MoneyGram is an international money transfer service that offers you ten minutes money transfers from anywhere in the world.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Fast, safe and reliable money transfer option where only the beneficiary is paid</li>\r\n 	<li data-uw-styling-context=\"true\">Accessible at any Co-operative Bank of South Sudan branches</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6067,448,'_accordions_4_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6068,448,'accordions_5_accordion_title','Fixed Deposit Account');
INSERT INTO `wp_postmeta` VALUES (6069,448,'_accordions_5_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6070,448,'accordions_5_accordion_description','<p data-uw-styling-context=\"true\">A Fixed Deposit Account is an investment account that requires a fixed amount of money to be invested for a fixed period at a fixed rate of interest. It offers you return on investment for funds to be used for your future cash flow.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Competitive interest rate</li>\r\n 	<li data-uw-styling-context=\"true\">Fast and easy to set up</li>\r\n 	<li data-uw-styling-context=\"true\">No transactional fee or monthly maintenance fees</li>\r\n 	<li data-uw-styling-context=\"true\">Varying maturity term periods to suit customer needs</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6071,448,'_accordions_5_accordion_description','field_624244aa1e3ef');
