INSERT INTO `wp_postmeta` VALUES (6198,502,'accordions_0_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">An identity card or passport (copy and original)</li>\r\n 	<li data-uw-styling-context=\"true\">Minimum opening balance where applicable</li>\r\n 	<li data-uw-styling-context=\"true\">A dully-filled Accounts Opening form</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6199,502,'_accordions_0_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6200,502,'accordions_1_accordion_title','What Is The Duration Of Opening An Account? ');
INSERT INTO `wp_postmeta` VALUES (6201,502,'_accordions_1_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6202,502,'accordions_1_accordion_description','An account is usually activated the same day a customer applies.<br data-uw-styling-context=\"true\" />The ATM Card is usually given a week later after the customer pays the required amount.<br data-uw-styling-context=\"true\" />In case of any delays, the customer should contact their branch for more information.');
INSERT INTO `wp_postmeta` VALUES (6203,502,'_accordions_1_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6204,502,'accordions_2_accordion_title','How Can One Retrieve Their Card Pin If They Forget Or Lose Them?');
INSERT INTO `wp_postmeta` VALUES (6205,502,'_accordions_2_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6206,502,'accordions_2_accordion_description','One should either go to the branch at the self-service area for assistance or call our contact centre team to have the pin reset. Email:<a href=\"mailto:customerservice@co-opbankss.com\" data-uw-styling-context=\"true\">customerservice@co-opbankss.com</a>');
INSERT INTO `wp_postmeta` VALUES (6207,502,'_accordions_2_accordion_description','field_624244aa1e3ef');
