INSERT INTO `wp_postmeta` VALUES (6210,502,'accordions_3_accordion_description','<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Identity card  both copy and original</li>\r\n 	<li data-uw-styling-context=\"true\">Minimum opening balance</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6211,502,'_accordions_3_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6212,502,'accordions_4_accordion_title','How Can I Get A Personal Loan?');
INSERT INTO `wp_postmeta` VALUES (6213,502,'_accordions_4_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6214,502,'accordions_4_accordion_description','<h3 data-uw-styling-context=\"true\">Minimum requirements include;</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Identity card both original and copy</li>\r\n 	<li data-uw-styling-context=\"true\">PIN card both original and copy</li>\r\n 	<li data-uw-styling-context=\"true\">Salary slips for the last 3 months</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6215,502,'_accordions_4_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6216,502,'accordions_5_accordion_title','How Long Does  It Take A Loan To Be Approved By The Bank?');
INSERT INTO `wp_postmeta` VALUES (6217,502,'_accordions_5_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6218,502,'accordions_5_accordion_description','The duration of getting a Co-op Bank loan usually depends on the type of loan one wants. i.e. personal loans, business loans, SME loans and others;<br data-uw-styling-context=\"true\" />The minimum time that one can get a loan is usually between 48 working hours and 2 weeks depends on the amount and type of loan.');
INSERT INTO `wp_postmeta` VALUES (6219,502,'_accordions_5_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6220,502,'accordions_6_accordion_title','Can One Get A Loan Using A Title Deed? And What Are The Requirements For One To Get A Loan?');
INSERT INTO `wp_postmeta` VALUES (6221,502,'_accordions_6_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6222,502,'accordions_6_accordion_description','Yes. It is possible for one to get a loan using a title deed but it depends on the type of loan needed.<br data-uw-styling-context=\"true\" />The requirements of getting a loan depend on the type of loan and can only be communicated at the branch after the customer identifies the type of loan');
INSERT INTO `wp_postmeta` VALUES (6223,502,'_accordions_6_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6224,502,'accordions_7_accordion_title','How Can I Open A Bank Account For My Business?');
INSERT INTO `wp_postmeta` VALUES (6225,502,'_accordions_7_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6226,502,'accordions_7_accordion_description','<h3 data-uw-styling-context=\"true\">All it requires is for you to come to the branch with the following:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Original and copy of ID and referee for individuals</li>\r\n 	<li data-uw-styling-context=\"true\">Originals and copies of MoU, Registration certificates and Directors resolution to open an account with the bank</li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (6227,502,'_accordions_7_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6228,502,'accordions_8_accordion_title','Cheque Maturity? ');
INSERT INTO `wp_postmeta` VALUES (6229,502,'_accordions_8_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6230,502,'accordions_8_accordion_description','Cheques take on average 48hours from time of deposit.');
INSERT INTO `wp_postmeta` VALUES (6231,502,'_accordions_8_accordion_description','field_624244aa1e3ef');
