INSERT INTO `wp_postmeta` VALUES (6233,502,'_accordions_9_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6234,502,'accordions_9_accordion_description','Yes, all branches are open on weekends from 8:30 am to Noon.<br data-uw-styling-context=\"true\" />Some branches have extended hours on weekdays and weekends. Kindly visit the Co-op Bank website<a href=\"https://co-opbankss.com/faqs/co-opbankss.com\" data-uw-styling-context=\"true\">www.co-opbankss.com</a>for more details or Call contact Centre Tel: +211 921 494 670 during working Hours.');
INSERT INTO `wp_postmeta` VALUES (6235,502,'_accordions_9_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6236,502,'accordions_10_accordion_title','What Is The Maximum Amount One Can Withdraw Daily From The ATM?');
INSERT INTO `wp_postmeta` VALUES (6237,502,'_accordions_10_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6238,502,'accordions_10_accordion_description','The maximum ATM withdrawal is SSP. 8,000');
