INSERT INTO `wp_postmeta` VALUES (6240,502,'accordions_11_accordion_title','Why Don\'t You Inform People Of New Things Through Text Messages?  ');
INSERT INTO `wp_postmeta` VALUES (6241,502,'_accordions_11_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6242,502,'accordions_11_accordion_description','We utilize a variety of communication channels to speak to our customers.<br data-uw-styling-context=\"true\" />Such as Facebook, Twitter, Co-op Bank website, Major Newspapers, Billboards, SMS, Email, Branch staff and ATM display screens.<br data-uw-styling-context=\"true\" />We do our best to keep our customers informed of any major changes.');
INSERT INTO `wp_postmeta` VALUES (6243,502,'_accordions_11_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6244,502,'accordions_12_accordion_title','Why Do Branches Delay In Processing The ATM Cards?');
INSERT INTO `wp_postmeta` VALUES (6245,502,'_accordions_12_accordion_title','field_6242449a1e3ee');
INSERT INTO `wp_postmeta` VALUES (6246,502,'accordions_12_accordion_description','The Bank does its best to ensure ATM cards are processed quickly.<br data-uw-styling-context=\"true\" />If there is any delay the branch will alert the customer.<br data-uw-styling-context=\"true\" />However, teller services are still available even when a customer does not have their ATM card.');
INSERT INTO `wp_postmeta` VALUES (6247,502,'_accordions_12_accordion_description','field_624244aa1e3ef');
INSERT INTO `wp_postmeta` VALUES (6248,502,'accordions_13_accordion_title','How Can An ATM Card Be Transferred From One Branch To another And Are There Any Charges?');
