INSERT INTO `wp_postmeta` VALUES (6250,502,'accordions_13_accordion_description','One has to confirm with the branch that the ATM card is available. Then go to the desired branch and fill a debit card transfer form.');
INSERT INTO `wp_postmeta` VALUES (6251,502,'_accordions_13_accordion_description','field_624244aa1e3ef');
