INSERT INTO `wp_postmeta` VALUES (6423,505,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">SWIFT is a worldwide money transfer platform that allows you to send and receive money across the world in the shortest time possible using your bank account.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Convenient, secure payment mode to recipients account</li>\r\n 	<li data-uw-styling-context=\"true\">Send and receive money from all non-sanctioned countries.</li>\r\n 	<li data-uw-styling-context=\"true\">Enjoy service at exclusive teller counters</li>\r\n 	<li data-uw-styling-context=\"true\">A sending charge for as low as USD 35</li>\r\n 	<li data-uw-styling-context=\"true\">Personalized customer service and so much more</li>\r\n</ul>');
