INSERT INTO `wp_postmeta` VALUES (6435,505,'accordions_5_accordion_description','<p data-uw-styling-context=\"true\">A Fixed Deposit Account is an investment account that requires a fixed amount of money to be invested for a fixed period at a fixed rate of interest. It offers you return on investment for funds to be used for your future cash flow.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Features &amp; Benefits:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Competitive interest rate</li>\r\n 	<li data-uw-styling-context=\"true\">Fast and easy to set up</li>\r\n 	<li data-uw-styling-context=\"true\">No transactional fee or monthly maintenance fees</li>\r\n 	<li data-uw-styling-context=\"true\">Varying maturity term periods to suit customer needs</li>\r\n</ul>');
