INSERT INTO `wp_postmeta` VALUES (6449,507,'accordions_0_accordion_description','<h3 data-uw-styling-context=\"true\">We offer a variety of services that include:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Formation of Co-operatives Movements/Saccos where members leverage on their small sums and participate in large investments from the pooled assets.</li>\r\n 	<li data-uw-styling-context=\"true\">Training of the society members across Cooperative sectors.</li>\r\n 	<li data-uw-styling-context=\"true\">Embrace a savings culture, investments, entrepreneurship and diversification.</li>\r\n 	<li data-uw-styling-context=\"true\">Providing innovative services and products to Co-operative across the country.</li>\r\n 	<li data-uw-styling-context=\"true\">Registration is easy.</li>\r\n</ul>\r\n<p data-uw-styling-context=\"true\">Visit our nearest branch or reach out to your Relationship Manager for more details</p>');
