INSERT INTO `wp_postmeta` VALUES (6453,507,'accordions_1_accordion_description','<p data-uw-styling-context=\"true\">Co-op Consultancy and Insurance Agency Ltd (C.C.I.A) is a wholly-owned subsidiary of Co-operative bank of South Sudan LTD incorporated to provide capacity building to co-operatives, pre-co-operatives, associations and other self-help groups to enhance social-economic development of the people through wealth creation.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">Capacity building services offered include:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Financial literacy, basic accounting and cultivating the culture of savings and credit</li>\r\n 	<li data-uw-styling-context=\"true\">Formation of primary Co-operatives</li>\r\n 	<li data-uw-styling-context=\"true\">Short term courses for Co-operative leaders</li>\r\n 	<li data-uw-styling-context=\"true\">Development of corporate governance tools</li>\r\n 	<li data-uw-styling-context=\"true\">Risk management</li>\r\n 	<li data-uw-styling-context=\"true\">Strategic plans</li>\r\n 	<li data-uw-styling-context=\"true\">Developing business plans and feasibility studies</li>\r\n 	<li data-uw-styling-context=\"true\">Governance and leadership development support services</li>\r\n</ul>');
