INSERT INTO `wp_postmeta` VALUES (6457,507,'accordions_2_accordion_description','<p data-uw-styling-context=\"true\">Our cash transfer model is purely on reimbursement basis for blue print international NGOs.</p>\r\n\r\n<h3 data-uw-styling-context=\"true\">The quoted price will depend on some of the following factors:</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">The amount to be transferred.</li>\r\n 	<li data-uw-styling-context=\"true\">The currency transferred (SSP or USD)</li>\r\n 	<li data-uw-styling-context=\"true\">The Reimbursement currency e.g. SSP reimbursed in USD or SSP reimbursed in SSP.</li>\r\n 	<li data-uw-styling-context=\"true\">Mode of cash transfer e.g. cash drop or mass payments to a number of beneficiaries;</li>\r\n 	<li data-uw-styling-context=\"true\">Logistics costs among other factors.</li>\r\n</ul>\r\n<h3 data-uw-styling-context=\"true\">Benefits of CIT Process</h3>\r\n<ul data-uw-styling-context=\"true\">\r\n 	<li data-uw-styling-context=\"true\">Same day delivery</li>\r\n 	<li data-uw-styling-context=\"true\">Negotiable pricing model</li>\r\n 	<li data-uw-styling-context=\"true\">Open to both account and non-account holders</li>\r\n 	<li data-uw-styling-context=\"true\">Covers all parts of South Sudan</li>\r\n 	<li data-uw-styling-context=\"true\">Availability of biometric system hence ease of accountability and reporting</li>\r\n</ul>\r\n<h3 data-uw-styling-context=\"true\">NB: This service is available to existing and non-existing customers.</h3>');
