INSERT INTO `wp_posts` VALUES (191,1,'2022-03-31 05:54:58','2022-03-31 05:54:58','<p data-uw-styling-context=\"true\">An investment account gives corporates/organizations an opportunity to invest funds that are waiting for further utilization at a negotiable competitive interest rate. You get an investment opportunity through our premium interest-earning fixed and call accounts, which provide the fund with avenues for short, medium and long-term investments. Funds will be placed in staggered tenors that are in line with the cash flow projections and budgetary schedules i.e. on call, (funds are available within 24-hrs notice); 1 month 2 months, 3 months, 6 months and 12 months tenors-(where funds will be available at maturity or 7 days notice).</p>\r\n<p data-uw-styling-context=\"true\"><strong data-uw-styling-context=\"true\">NB:</strong>Please note that funds may be invested prior to opening any other accounts</p>','Call & Deposit Account','','publish','closed','closed','','call-deposit-account','','','2022-04-12 13:25:12','2022-04-12 13:25:12','',0,'http://ss.local/?post_type=accounts&#038;p=191',0,'accounts','',0);
