INSERT INTO `wp_posts` VALUES (194,1,'2022-03-31 05:57:53','2022-03-31 05:57:53','<h3 data-uw-styling-context=\"true\">Rest your medical worries on our shoulders</h3>\r\n<p data-uw-styling-context=\"true\">Afya plan ensures that your health is of primary importance.<br data-uw-styling-context=\"true\" />This cover provides protection from significant medical expensed through its extensive network of healthcare professionals and service providers.<br data-uw-styling-context=\"true\" />This cover is suitable for individuals, families, groups and organizations.</p>','Afya','','publish','closed','closed','','afya','','','2022-04-12 14:35:35','2022-04-12 14:35:35','',0,'http://ss.local/?post_type=bancassurance&#038;p=194',0,'bancassurance','',0);
INSERT INTO `wp_posts` VALUES (195,1,'2022-03-31 06:09:03','2022-03-31 06:09:03','<h3 data-uw-styling-context=\"true\">Co-op Medicare</h3>\r\n<strong data-uw-styling-context=\"true\">Having a pre-existing condition should not prevent you from getting a medical cover!</strong>\r\n<p data-uw-styling-context=\"true\">Co-op MediCare is plan is designed to suit your health needs. It takes care of the expensive medical treatments incurred for both inpatient and outpatient.<br data-uw-styling-context=\"true\" />Your health is precious to you. It enables you to live a happier life. With MediCare, you do not have to worry about not having enough money for treatment in case you fall sick.<br data-uw-styling-context=\"true\" />Co-op MediCare is suitable for individuals, families, groups and organizations.</p>','Medicare','','publish','closed','closed','','medicare','','','2022-04-12 14:33:59','2022-04-12 14:33:59','',0,'http://ss.local/?post_type=bancassurance&#038;p=195',0,'bancassurance','',0);
