INSERT INTO `wp_posts` VALUES (199,1,'2022-03-31 06:09:01','2022-03-31 06:09:01','<h3 data-uw-styling-context=\"true\">Banking opportunity to the Diaspora with a special focus on investments</h3>\r\nOur Diaspora Account is available to all South Sudanese nationals living abroad. This account allows South Sudanese in diaspora the same banking opportunities that they would have access to in South Sudan with a special focus on investments.\r\n<h3 data-uw-styling-context=\"true\">Open a Diaspora Account today!</h3>','Diaspora Account','','publish','closed','closed','','diaspora-account','','','2022-04-12 13:20:09','2022-04-12 13:20:09','',0,'http://ss.local/?post_type=accounts&#038;p=199',0,'accounts','',0);
