INSERT INTO `wp_posts` VALUES (267,1,'2022-04-04 08:27:51','2022-04-04 08:27:51','<p data-uw-styling-context=\"true\">The Co-operative Bank South Sudan Limited is a commercial bank whose aim is to bring about economic empowerment to the people of South Sudan through the establishment and growth of a strong Cooperative movement in every sector of the economy.</p>\r\n<p data-uw-styling-context=\"true\">The bank offers innovative conventional banking services to Cooperatives, SMEs, Microfinance, International Organizations, Institutions, Corporates, Individuals, Government Ministries and Quasi-Government Agencies, private and public sector customers with a full range of banking services for a positive impact on the economy.</p>\r\n<p data-uw-styling-context=\"true\">Co-operative Bank of South Sudan (COBSS) has been established as a strategic joint venture between the Government of the Republic of South Sudan (RSS) on behalf of the co-operative movement in South Sudan and Co-operative Bank of Kenya (COPK).</p>','About Us','','inherit','closed','closed','','26-revision-v1','','','2022-04-04 08:27:51','2022-04-04 08:27:51','',26,'http://ss.local/?p=267',0,'revision','',0);
